document.getElementById('the_selector').onchange = function() {
  if (this.selectedIndex == 1) {
    document.getElementById('one_field').style.display = 'block';
    document.getElementById('the_other_field').style.display = 'none';
  } else if (this.selectedIndex == 2) {
    document.getElementById('one_field').style.display = 'none';
    document.getElementById('the_other_field').style.display = 'block';
}
