//defino las entradas para los sensores CNY70
const int sectorUno = A1;
const int sectorDos = A2;
const int sectorTres = A3;
const int sectorCuatro = A4;
const int sectorCinco = A5;

//defino tres variables, una por sector
int valorUno = 0;
int valorDos = 0;
int valorTres = 0;
int valorCuatro = 0;
int valorCinco = 0;
int valorDigital = 0;


void setup(){
  // inicializo las entradas
   pinMode(sectorUno, INPUT);
   pinMode(sectorDos, INPUT);
   pinMode(sectorTres, INPUT);
   pinMode(sectorCuatro, INPUT);
   pinMode(sectorCinco,INPUT);
    // inicializo las comunicaciones serial a 9600bps 
   // para ver resultado clicar Serial Monitor 
   // la ventanita de la derecha en el IDE
  Serial.begin(9600); 
}

void loop(){
  // leo los valores en analogico y los paso a digital
  valorUno = analogRead (sectorUno);
  if (valorUno < 600) {
    valorUno = 0;
  } else{
     valorUno = 1;
  } 
  valorDos = analogRead (sectorDos);
  if (valorDos < 400) {
    valorDos = 0;
  } else{
     valorDos = 1;
  }
  valorTres = analogRead (sectorTres);
   if (valorTres < 400) {
    valorTres = 0;
  } else{
     valorTres = 1;
  }
  valorCuatro = analogRead (sectorCuatro);
  if (valorCuatro < 600) {
    valorCuatro = 0;
  } else{
     valorCuatro = 1;
  }
  valorCinco = analogRead (sectorCinco);
   if (valorTres < 400) {
    valorCinco = 0;
  } else{
     valorCinco = 1;
  }
  //imprimo los valores por el Serial Monitor
  Serial.print("sensorUno = " );                       
  //Serial.print(sensorValue);      
  //Serial.print("\t output = ");      

  Serial.println(valorUno); 
  Serial.print("sensorDos = " );    
  Serial.println(valorDos); 
  Serial.print("sensorTres = " );    
  Serial.println(valorTres); 
  Serial.print("sensorCuatro = " );    
  Serial.println(valorCuatro); 
  Serial.print("sensorCinco = " );    
  Serial.println(valorCinco); 
  valorDigital = valorUno*10000+valorDos*1000+valorTres*100+valorCuatro*10+valorCinco;
   Serial.print("valorDigital = " );    
  Serial.println(valorDigital); 


  if (valorDigital == 1000 || valorDigital ==1100 || valorDigital ==1010 || valorDigital ==110 || valorDigital ==10 || valorDigital ==0 || valorDigital ==100){
     Serial.print("N" ); 
     Serial.println("");
