.tabset-over a span {    
	-webkit-transition-property: background-color, color;
	-webkit-transition-duration: 2s, 2s;
	-webkit-transition-timing-function: ease, ease;
}
.tabset-over a:link span, .tabset-over a:visited span {
	background-color: rgba(255,0,0,0);
	color:#000000;

}
.tabset-over a:hover span {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(255, 0, 0, 1)), to(rgba(0, 255, 0, 1)));
    color:#ffffff;
}

.tabset-over a span {    
	-webkit-transition:color .2s linear;
	-webkit-transition:background-image 2s linear;
}
.tabset-over a:link span, .tabset-over a:visited span {
	background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgba(255, 0, 0, 0)), to(rgba(0, 255, 0, 0)));
	color:#000000;

}
.tabset-over a:hover span {
	background-color: #067c78;
    background-image: url(../images/bg-tabs-hover.gif);
    background-repeat: repeat-x;
    background-image: -moz-linear-gradient(100% 100% 90deg, #067c78, #5bb5b1);
    background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgba(255, 0, 0, 1)), to(rgba(0, 255, 0, 1)));
    color:#ffffff;
}
