public static final String PLACE_SEPARATOR = "/";

public static String buildRelativePath(PlaceManager placeManager,
		PlaceRequest placeRequest) {

	StringBuilder navigation = new StringBuilder();
	boolean addSeparator = false;
	for (PlaceRequest node : placeManager.getCurrentPlaceHierarchy()) {
		if (addSeparator) {
			navigation.append(PLACE_SEPARATOR);
		}
		addSeparator = true;

		if (node.hasSameNameToken(placeRequest)) {
			navigation.append(placeManager.buildHistoryToken(placeRequest));
		} else {
			navigation.append(placeManager.buildHistoryToken(node));
		}
	}

	return navigation.toString();
}

public static final String PLACE_SEPARATOR = "/";

public static String buildRelativePath(PlaceManager placeManager,
		PlaceRequest placeRequest) {

	StringBuilder navigation = new StringBuilder();
	boolean addSeparator = false;
	boolean placeAdded = false;
	for (PlaceRequest node : placeManager.getCurrentPlaceHierarchy()) {
		if (addSeparator) {
			navigation.append(PLACE_SEPARATOR);
		}
		addSeparator = true;

		if (node.hasSameNameToken(placeRequest)) {
			navigation.append(placeManager.buildHistoryToken(placeRequest));
			placeAdded = true;
		} else {
			navigation.append(placeManager.buildHistoryToken(node));
		}
	}

	if (!placeAdded) {
		if (addSeparator) {
			navigation.append(PLACE_SEPARATOR);
		}
		navigation.append(placeManager.buildHistoryToken(placeRequest));
	}

	return navigation.toString();
}

