// ==UserScript==
// @name           render latex via Mathjax
// @namespace      pcrmod
// @description    self explanatory. thanks to mathjax's free server
// @include        http://arxiv.org/*
// @include        http*://mail.google.com/mail/*v*=pt*
// @include        http*://mail.google.com/mail/h/*
// @include        http://convore.com/*
// @include        https://convore.com/*
// ==/UserScript==
//
//
var mathjaxConfig = 'MathJax.Hub.Config({"HTML-CSS": { preferredFont: "TeX", availableFonts: ["STIX","TeX"] }, tex2jax: { inlineMath: [ ["$","$"], ["\\\\(","\\\\)"] ], displayMath: [ ["$$","$$"] ], processEscapes: true, ignoreClass: "tex2jax_ignore|dno" }, TeX: { noUndefined: { attributes: { mathcolor: "red", mathbackground: "#FFEEEE", mathsize: "90%" } } }, messageStyle: "none"});';



var mathjaxServer = "http://cdn.mathjax.org/mathjax/latest/MathJax.js?config=TeX-AMS_HTML-full";


var configscript = document.createElement('script');
configscript.type="text/x-mathjax-config";
foo = document.createTextNode(mathjaxConfig);
configscript.appendChild(foo);
//configscript.appendChild(mathjaxQueue);

var script = document.createElement('script');
script.type="text/javascript";
script.src = mathjaxServer;

try { 
    document.getElementsByTagName('head')[0].appendChild(configscript); 
    document.getElementsByTagName('head')[0].appendChild(script); 
} catch(e) {}
